mapper.select:
SELECT tpi.target_date, tci.cust_name, tii.manufac, tii.major_class, tii.sub_class, tii.item_name, tpi.goal_qut, tpi.perform_qut, tpi.plan_note, tpi.loginID, tpi.plan_num FROM tb_plan_info tpi LEFT OUTER JOIN tb_cust_info tci ON tpi.cust_id = tci.cust_id LEFT OUTER JOIN tb_item_info tii ON tpi.item_code = tii.item_code
AND tii.manufac = #{searchManufac}
AND tii.major_class = #{searchMajor}
AND tii.sub_class = #{searchSub}
AND tii.item_name LIKE CONCAT('%', #{searchItemName}, '%')
AND tpi.target_date = #{searchDate}
AND tpi.loginID = #{loginID}
ORDER BY tpi.target_date LIMIT #{startSeq}, #{pageSize}

SELECT
	COUNT(*)
FROM tb_plan_info tpi
LEFT OUTER JOIN tb_cust_info tci ON
    tpi.cust_id = tci.cust_id
LEFT OUTER JOIN tb_item_info tii ON
    tpi.item_code = tii.item_code


		AND tii.manufac = #{searchManufac}



		AND tii.major_class = #{searchMajor}


		AND tii.sub_class = #{searchSub}


		AND tii.item_name LIKE CONCAT('%', #{searchItemName}, '%')


		AND tpi.target_date = #{searchDate}

	AND tpi.loginID = #{loginID}

		SELECT
			DISTINCT item_name,
			item_code
    	FROM 
    		tb_item_info
    	WHERE 
    		manufac = #{manufac}
      		AND major_class = #{major_class}
      		AND sub_class = #{sub_class}

SELECT tpi.loginID, tu.name, tpi.target_date, tii.manufac, tii.major_class, tii.sub_class, tii.item_code, tii.item_name, tpi.goal_qut, tpi.perform_qut, tpi.plan_note, tpi.plan_num FROM tb_plan_info tpi LEFT OUTER JOIN tb_item_info tii ON tpi.item_code = tii.item_code LEFT OUTER JOIN tb_userinfo tu ON tpi.loginID = tu.loginID
AND tpi.loginID LIKE CONCAT('%', #{searchloginID}, '%')
AND tii.manufac = #{searchManufac}
AND tpi.target_date = #{searchDate}
AND tii.major_class = #{searchMajor}
AND tii.sub_class = #{searchSub}
AND tii.item_name LIKE CONCAT('%', #{searchItemName}, '%')
LIMIT #{startSeq}, #{pageSize}

SELECT
	COUNT(*)
FROM tb_plan_info tpi
LEFT OUTER JOIN tb_item_info tii ON
    tpi.item_code = tii.item_code
LEFT OUTER JOIN tb_userinfo tu ON
    tpi.loginID = tu.loginID


		AND tpi.loginID LIKE CONCAT('%', #{searchloginID}, '%')


		AND tii.manufac = #{searchManufac}


		AND tpi.target_date = #{searchDate}



		AND tii.major_class = #{searchMajor}


		AND tii.sub_class = #{searchSub}


		AND tii.item_name LIKE CONCAT('%', #{searchItemName}, '%')
